/*#region Global Styling*/

//-----COLORS-----

$black: #000000;
$black-5: #0d0d0d;
$black-25: #404040;
$white: #ffffff;
$white-5: #f2f2f2;
$white-25: #bfbfbf;
$dark: #310404;
$dark-brown: #630404;
$red: #890e08;
$yellow: #fbbe08;
$orange: #eca104;
$dark-orange: #b05108;

//-----FONTS-----

@font-face {
  font-family: 'NugieItalic';
  src: local('Nugieitalic'), url(../fonts/NugieItalic.ttf) format('truetype');
	font-weight: normal;
  font-style: normal;
}

@font-face {
  font-family: 'NugieRegular';
  src: local('NugieRegular'), url(../fonts/NugieRegular.ttf) format('truetype');
	font-weight: normal;
  font-style: normal;
}

$header-font: Apple Chancery, URW Chancery L, Snell Roundhand, Cursive;
$primary-font: Verdana, Arial, Times, Courier, Georgia;


@mixin pirate-reg {
	color: $dark;
	font-family: NugieRegular;
}

@mixin pirate-italic {
	color: $dark;
	font-family: NugieItalic;
}

@mixin primary-font {
	color: $dark;
	font-family: $primary-font;
}

//-----HTML-----

* {
	box-sizing: border-box;
}

html {
  scroll-behavior: smooth;
}

body {
	background: url('../images/parchment.jpg') no-repeat center center fixed; 
  -webkit-background-size: cover;
  -moz-background-size: cover;
  -o-background-size: cover;
  background-size: cover;
}

a:-webkit-any-link {
  color: $red;
  text-decoration: underline;
  cursor: pointer;
}

ul {
	display: block;
	list-style-type: disc;
	margin-block-start: 0.25em;
	margin-block-end: 0.25em;
	margin-inline-start: 0px;
	margin-inline-end: 0px;
	padding-inline-start: 40px;
}

/*#endregion Global Styling*/

/*#region Scaffolding*/

.container-A {
	width: 100%;
}

.container-B {
  width: 100%;
	max-width: 1000px;
	margin: 0 auto;
}

.col-1 {width: 8.33%;}
.col-2 {width: 16.66%;}
.col-3 {width: 25%;}
.col-4 {width: 33.33%;}
.col-5 {width: 41.66%;}
.col-6 {width: 50%;}
.col-7 {width: 58.33%;}
.col-8 {width: 66.66%;}
.col-9 {width: 75%;}
.col-10 {width: 83.33%;}
.col-11 {width: 91.66%;}
.col-12 {width: 100%;}

.row::after {
  content: "";
  clear: both;
  display: table;
}

[class*="col-"] {
  float: left;
}

@mixin row-center {
	display: flex;
	flex-flow: row wrap;
	justify-content: center;
	align-items: center;
}

@mixin col-center {
	display: flex;
	flex-flow: col wrap;
	justify-content: center;
	align-items: center;
}

.temp-spacer {
	height: 1000px;
}

//-----MEDIA QUERY MIXINS
@mixin mobile {
	@media (max-width: 768px) {
		@content;
	}
}

@mixin web {
	@media (min-width: 769px) {
    @content;
  }
}

/*#endregion Scaffolding*/

/*#region Common Styling*/

$border-primary: 1px solid $black;

$center: 0px auto;

.center-wrap {
	text-align: center;
}

.page-title {
	@include pirate-reg;
	@include col-center;
	font-size: calc(1.75rem + 2vmin);
	text-decoration: underline;
	margin-top: 10px;
}

.paragraph-heading {
  @include pirate-italic;
	font-size: calc(1.5rem + 1vmin);
	text-decoration: underline;
	display: flex;
	flex-flow: row nowrap;
	justify-content: center;
	align-items: center;
	vertical-align: middle;
	margin-top: 15px;
}

.paragraph {
  color: $dark;
	font-size: calc(.9rem + .7vmin);
	font-family: $primary-font;
	font-weight: bold;
	margin: 10px 5% 0;
	text-align: justify;
	text-indent: 1em;
	word-wrap: break-all;
}

.dagger1 {
	width: 43px;
	vertical-align: middle;
	transform: scaleX(-1) rotate(75deg);
	margin-right: 10px;
}

.dagger2 {
	width: 43px;
	transform: rotate(75deg);
	margin-left: 10px;
}

.list-wrap {
	display: flex;
	flex-flow: row wrap;
	justify-content: space-between;
	align-items: flex-start;
  padding: 0 5%;
	margin-top: 10px;
}

.list-item {
	margin-top: 10px;
}

#spyglass {
	width: calc(2rem + 2vmin);
	min-width: 25px;
	max-width: 45px;
	transform: rotate(295deg);
	vertical-align: middle;
	margin-right: 15px;
}

@media (max-width: 575px) {
	.paragraph {
		text-align: left;
	}	
}

