/*#endregion Common Styling*/

/*#region Title*/

#title-wrap {
  @include row-center;
	margin-top: 15px;
}

.title {
  @include pirate-reg;
	font-size: calc(3rem + 3vmin);
	filter: drop-shadow(6px 6px 8px $dark);
}

#title-part1 {
	margin-right: 10px;
	order: 1;
}

#title-part2 {
	margin-left: 10px;
	order: 3;
}

#crossed-keys {
	width: calc(2.5rem + 21vmin);
	min-width: 125px;
	max-width: 216px;
	order: 2;
	filter: drop-shadow(4px 4px 8px $dark);
}

.sub-wrap {
  @include row-center;
}

#sub-title {
  @include pirate-reg;
	font-size: calc(2.25rem + 2vmin);
	margin-top: 10px;
}

#breakline1 {
	width: 96%;
	min-width: 372.5px;
	max-width: 950px;
	height: 4.5vw;
	min-height: 22px;
	max-height: 45px;
	margin: .75vmin 0;
}

/*#endregion Title*/

/*#region NavBar*/

.nav-wrap {
	display: flex;
	flex-flow: row nowrap;
	justify-content: space-around;
	align-items: center;
	height: 61px;
	width: 100%;
	max-width: 375px;
	padding: 4px;
	backdrop-filter: blur(8px);
	border-radius: 15px;
	margin: 0px auto;
}

.nav-icon {
	width: calc(2rem + 3vmin);
	min-width: 45px;
	max-width: 55px;
	vertical-align: middle;
}

