/*#endregion NavBar*/

/*#region Home Page*/

.skills-list {
	@include pirate-reg;
	list-style-image: url('../images/smallsword.png');
	font-size: calc(1.25rem + 1.5vmin);
	margin-left: 30px;
}

/*#endregion Home Page*/

/*#region Projects Page*/

.project-list {
  @include pirate-reg;
	font-size: calc(1.25rem + 1vmin);
	list-style-image: url('../images/smallsword.png');
	margin-left: 30px;
}

.list-item {
	margin-top: 10px;
}

/*#endregion Projects Page*/

/*#region About Page*/

.moreInfoVisible {
  display: internal;
}

.moreInfoHidden {
  display: none;
}

.button-wrap {
	@include col-center;
	margin-top: 15px;
	vertical-align: middle;
}

.read-more {  
  @include pirate-italic;
	font-size: calc(1.5rem + 1vmin);
	text-decoration: underline;
	color: $red;
	background: none;
	border: none;
}

.read-less {
  @include pirate-reg;
	font-size: calc(1.25rem + 1vmin);
	color: $red;
	background: transparent;
	border: 2px solid $red;
	padding: 5px;
	border-radius: 7px;
	margin-top: 10px;
}

/*#endregion About Page*/

/*#region Credentials Page*/

.cred-list {
	@include pirate-reg;
	list-style-image: url('../images/smallsword.png');
	font-size: calc(1.25rem + 1.25vmin);
	margin-left: 30px;
}

/*#endregion Credentials Page*/

/*#region Footer*/

#resume-wrap {
	margin-top: 20px;
	text-align: center;
}

#resume-link {
  @include pirate-reg;
	font-size: calc(1.35rem + 1.5vmin);
	vertical-align: middle;
	color: $red;
}

#breakline2 {
	width: 96%;
	min-width: 372.5px;
	max-width: 950px;
	min-height: 25px;
	max-height: 42px;
	transform: rotate(180deg);
	margin-top: 20px;
}

.glyph-wrap {
	@include row-center;
	margin: 15px auto 35px;
	padding: 0px 10%;
}

.glyph {
	width: calc(2rem + 2.5vmin);
	vertical-align: middle;
	margin: 15px 45px;
}

#linkedin {
	order: 1;
}

#github {
	order: 2;
}

#pigeon {
  width: calc(3rem + 3vmin);
	vertical-align: middle;
	margin: 15px 45px;
	order: 3;
}

#medium {
	order: 4;
}

#youtube {
	order: 5;
}

/*#endregion Footer*/
